/** 模型类别到图标组件的映射 */
export type ModelCategory = 'claude' | 'gemini' | 'kimi' | 'gpt' | 'qwen' | 'grok' | 'auto';

/** 获取模型厂商图标组件 */
export const getProviderIcon = (category: ModelCategory | string, size = 16): React.ReactNode => {
  switch (category) {
    case 'gemini':
      return <GeminiIcon size={size} />;
    case 'claude':
      return <ClaudeIcon size={size} />;
    case 'gpt':
      return <GPTIcon size={size} />;
    case 'grok':
      return <GrokIcon size={size} />;
    case 'auto':
      return <AutoIcon size={size} />;
    // kimi 和 qwen 暂时使用默认图标
    case 'kimi':
    case 'qwen':
    default:
      return <DefaultProviderIcon size={size} />;
  }
};
